definition.script:
pipeline {
    agent any
    environment {
        DOCKER_COMPOSE_FILE = 'docker-compose.yml' // Путь к вашему файлу docker-compose.yml
    }
    stages {
        stage('Checkout') {
            steps {
                git branch: 'main', url: 'https://github.com/grogu27/jenkins_avs.git'
            }
        }
        stage('Build and Deploy with Docker Compose') {
            steps {
                script {
                    // Перейдем в каталог, где расположен docker-compose.yml
                    dir('.') {
                        // Запуск сборки и деплоя с помощью docker-compose
                        sh 'docker-compose -f ${DOCKER_COMPOSE_FILE} up -d --build'
                    }
                }
            }
        }
    }
    triggers {
        pollSCM('*/10 * * * * *')
    }
    post{
        always{
            echo 'pipeline finished'
        }
        success{
            echo ':)'
        }
        failure{
            echo ':('
        }
    }
}
