definition.script:
pipeline {
    agent any
    stages {
        stage('Checkout') {
            steps {
                git branch: 'main', url: 'https://github.com/grogu27/jenkins_avs.git'
            }
        }
        stage('Build Docker Image') {
            steps {
                script {
                    // Сборка Docker образа
                    echo 'Building Docker image...'
                    sh 'docker build -t my-image:latest .'  // Сборка образа с тегом 'my-image'
                }
            }
        }
        stage('Build and Deploy with Docker Compose') {
            steps {
                script {
                    // Перейдем в каталог, где расположен docker-compose.yml
                    dir('.') {
                        // Запуск сборки и деплоя с помощью docker-compose
                        sh "docker-compose -f docker-compose.yml up -d --build"
                    }
                }
            }
        }
    }
    triggers {
        pollSCM('*/10 * * * *')  // Проверка изменений каждые 10 минут
    }
    post {
        always {
            echo 'Pipeline finished'
        }
        success {
            echo ':)'
        }
        failure {
            echo ':('
        }
    }
}
