definition.script:
pipeline {
    agent any
    environment {
        DOCKER_COMPOSE_VERSION = '1.29.2'
        DOCKER_COMPOSE_BIN = "${env.HOME}/docker-compose" // Путь для установки docker-compose
    }
    stages {
        stage('Checkout') {
            steps {
                git branch: 'main', url: 'https://github.com/grogu27/jenkins_avs.git'
            }
        }
        stage('Install Docker Compose') {
            steps {
                script {
                    // Проверка, установлен ли docker-compose
                    if (!fileExists("${DOCKER_COMPOSE_BIN}")) {
                        echo 'docker-compose не найден, установка...'
                        sh """
                            curl -L https://github.com/docker/compose/releases/download/${DOCKER_COMPOSE_VERSION}/docker-compose-Linux-x86_64 -o ${DOCKER_COMPOSE_BIN}
                            chmod +x ${DOCKER_COMPOSE_BIN}
                        """
                    } else {
                        echo 'docker-compose уже установлен.'
                    }
                }
            }
        }
        stage('Build and Deploy with Docker Compose') {
            steps {
                script {
                    // Перейдем в каталог, где расположен docker-compose.yml
                    dir('.') {
                        // Запуск сборки и деплоя с помощью docker-compose
                        sh "${DOCKER_COMPOSE_BIN} -f docker-compose.yml up -d --build"
                    }
                }
            }
        }
    }
    triggers {
        pollSCM('*/10 * * * *')  // Проверка изменений каждые 10 минут
    }
    post {
        always {
            echo 'Pipeline finished'
        }
        success {
            echo ':)'
        }
        failure {
            echo ':('
        }
    }
}
